import { configureStore, createAction, createReducer } from '@reduxjs/toolkit';

// Define initial state
const initialState = {
  contacts: [],
  filter: '',
};

// Define actions
const addContact = createAction('contacts/add');
const deleteContact = createAction('contacts/delete');
const updateFilter = createAction('filter/update');

// Define reducers
const contactsReducer = createReducer(initialState.contacts, {
  [addContact]: (state, action) => [...state, action.payload],
  [deleteContact]: (state, action) =>
    state.filter(contact => contact.id !== action.payload),
});

const filterReducer = createReducer(initialState.filter, {
  [updateFilter]: (_, action) => action.payload,
});

export const store = configureStore({
  reducer: {
    contacts: contactsReducer,
    filter: filterReducer,
  },
});
